import{_ as t}from"./plugin-vue_export-helper-c27b6911.js";import{o as d,c as r,e as a}from"./app-209572b3.js";const e="/assets/image-1-d336f4ca.png",s={},i=a('<div class="hint-container tip"><p class="hint-container-title">建议使用搜索</p><p>菜单是一个内容极大的系统，文章很长~<br> 如果你知道你想要什么，建议使用Ctrl+K / Ctrl+F搜索！</p></div><h2 id="一层菜单" tabindex="-1"><a class="header-anchor" href="#一层菜单" aria-hidden="true">#</a> 一层菜单</h2><p>当你按下Esc时，你将在屏幕中心看到如下图的界面。（VR端的随身菜单会出现在你按下菜单那键的手中）<br><img src="https://vrchat-cn-wiki.oss-cn-shanghai.aliyuncs.com/随身菜单.png" alt="" width="400" loading="lazy"></p><h3 id="主界面翻译" tabindex="-1"><a class="header-anchor" href="#主界面翻译" aria-hidden="true">#</a> 主界面翻译</h3><p>一层菜单的按钮翻译如下：<br><img src="https://vrchat-cn-wiki.oss-cn-shanghai.aliyuncs.com/随身菜单翻译.png" alt="" loading="lazy"></p><p>点击表格中的名称查看相关教程。</p><table><thead><tr><th>按钮</th><th>说明</th></tr></thead><tbody><tr><td>广告/公告区</td><td>官方用来推送资讯的栏位</td></tr><tr><td>系统菜单按钮</td><td>位于随身菜单右上角，点击后会根据当前随身菜单页面打开系统菜单。<br> 例如在房间信息页，点击系统菜单按钮就会打开房间页的系统菜单</td></tr><tr><td>世界列表</td><td>点击后打开系统菜单的世界页面</td></tr><tr><td>化身列表</td><td>点击后打开系统菜单的化身页面</td></tr><tr><td>联系人</td><td>点击后打开系统菜单的联系人页面，<br> 联系人可以理解成你的好友列表。但是你还可以看到你的黑名单列表。</td></tr><tr><td>群组列表</td><td>点击后打开群组列表，<br> 群组可以理解成社团，公会。<br> <strong>随便加入，但建设群组是氪金功能哦！</strong></td></tr><tr><td>回家</td><td>点击后，经过二次确认回到你的初始地图。<br>默认情况下，初始地图是VRChat Home</td></tr><tr><td>重生</td><td>点击后，立即回到所处地图的出生点<br>可被世界作者更改</td></tr><tr><td>选择用户</td><td>点击后菜单隐藏，屏幕中心可以选中用户<br>鸡肋功能，凑数用的。<br><strong>在菜单状态下，鼠标移出菜单范围就可以选择其他用户</strong></td></tr><tr><td>安全模式</td><td>点击后可快速切换安全模式</td></tr><tr><td>麦克风开关</td><td>点击后切换麦克风的开关</td></tr><tr><td>首页</td><td>切换到随身菜单的首页</td></tr><tr><td>通知</td><td>切换到通知页<br>好友申请，申请加入等消息会在此处</td></tr><tr><td>当前房间信息</td><td>切换到房间信息页</td></tr><tr><td>相机菜单</td><td>切换到相机菜单</td></tr><tr><td>音频设置</td><td>切换到音频设置菜单，可以设置各种音量大小和音频设备等功能</td></tr><tr><td>设置</td><td>切换到快捷设置页</td></tr><tr><td>紧急安全模式</td><td>点击后进入紧急安全模式，屏蔽所有非好友用户的化身<br>快捷键为 Shfit+ESC<br><strong>快捷键只能启动，不能关闭</strong></td></tr></tbody></table><h3 id="两翼菜单" tabindex="-1"><a class="header-anchor" href="#两翼菜单" aria-hidden="true">#</a> 两翼菜单</h3><p>现在，请点击菜单两侧的翅膀，将其完全展开<br><img src="'+e+'" alt="" loading="lazy"></p><p>两翼菜单是一种快捷信息菜单，可以快速的为你提供大量信息。 由以下部分构成：</p><table><thead><tr><th>按钮</th><th>说明</th></tr></thead><tbody><tr><td>Profile</td><td>个人信息查阅</td></tr><tr><td>Friends</td><td>好友信息查阅</td></tr><tr><td>Groups</td><td>群组信息查阅</td></tr><tr><td>Avatars</td><td>用户化身列表查阅</td></tr><tr><td>Worlds</td><td>查看世界列表，仅限于<br>- 你上传的世界<br>- 你最近去过的世界<br>- 你收藏的世界</td></tr><tr><td>Emoji</td><td>系统表情菜单</td></tr><tr><td>Expressions</td><td>化身动画菜单，圆盘菜单的另一种显示方式</td></tr><tr><td>Explore</td><td>讨论页，内容是VRChat官方的各种资讯</td></tr></tbody></table>',11),n=[i];function h(o,c){return d(),r("div",null,n)}const p=t(s,[["render",h],["__file","05SuiShen.html.vue"]]);export{p as default};
